import React from 'react';
import './SearchPanel.css';

class SearchPanel extends React.Component{
    state = {
        searchValue: '',
        _inputWidth: '50%'
    }
    handleInputChange = (e) => {
        this.setState({
            searchValue: e.target.value
        },() => {
            this.props.onSearch(this.state.searchValue);
        });

    }
    expandInput = () => {
        this.setState({_inputWidth: '100%'})
    }
    minimizeInput = () => {
        this.setState({_inputWidth: '50%'})
    }

    render(){
        return (
            <div

                className='search_cont d-flex flex-direction-column w-25'>
                <div >
                    <input
                        onClick={this.expandInput}
                        onBlur={this.minimizeInput}
                        className='search_input w-100'
                        style={{width: this.state._inputWidth}}
                        type="search"
                        onChange={this.handleInputChange}
                        value={this.state.searchValue}
                    />
                </div>
                <div style={{display: this.props.searchResults.length ? 'block' : 'none'}}>
                    HIDDEN PART
                </div>
            </div>
        )
    }
}

export default SearchPanel;
