import React from 'react';
import PropTypes from 'prop-types';
import './CategoryPage.css';
import NewsItem from "../NewsItem/NewsItem";



const CategoryPage = (props) => {
    let {category} = props;
    category = category.charAt(0).toUpperCase() + category.slice(1);
    if(!props.newses.length){
        return (
            <div className='text-center no_content'>
                There is no article in «{category}» Category.
            </div>
        )
    }
    return (
        <div className='category_page_cont w-50 mx-auto'>
            {
                props.newses.map(news => (
                    <NewsItem
                        id={news._id}
                        title={news.title}
                        content={news.content}
                        imgUrl={news.imgUrl}
                        category={news.category}
                        author={news.author}
                        source={news.sourceUrl}
                        description={news.description}
                        createdAt={news.createdAt}
                    />
                ))
            }
        </div>
    )
}
CategoryPage.propTypes = {
    newses: PropTypes.array.isRequired
}
export default CategoryPage;
