import React from 'react';
import './Login.css';
import Loading from '../Loading/Loading';
import LoginForm from '../LoginForm/LoginForm';
import {Redirect} from 'react-router-dom';
import {client} from '../../Client';


class Login extends React.Component{
    state = {
        _loginInProcess: false,
        _loginFailed: false,
        _shouldRedirect: false
    }
    login = (data) => {
        const {username, password} = data;

        client.login({username, password})
            .then(() => {

            })
    }
    redirectPath = () => {
        const locationState = this.porps.location.state;
        const pathname = (
            locationState && locationState.from && locationState.from.pathname
        );
        return pathname || '/'
    }
    render(){
        if(this.state._shouldRedirect){
            return (
                <Redirect to={this.redirectPath()}/>
            )
        } else if(this.state._loginInProcess) {
            return (
                <Loading velocity='slow'/>
            )
        } else {
            return (
                <LoginForm
                    login={this.login}
                    loginFailed={this.state._loginFailed}
                />
            )
        }

    }
}

export default Login;
