import React from 'react';
import './Modal.css';


class Modal extends React.Component {
    state = {
        _windowMouseOvered: false
    }
    onOutClick = () => {
        if(!this.state._windowMouseOvered){
            this.props.onClose();
        }
    }
    render(){
        return (
            <div
                onClick={this.onOutClick}
                className={`modal_background ${this.props.isOpen ? 'd-block' : 'd-none'}`}>
                <div

                    onMouseOver={() => (this.setState({_windowsMouseOvered: true}))}
                    onMouseOut={() => (this.setState({_windowsMouseOvered: false}))}
                    className={`modal_window w-50 mx-auto`}>
                    <div className='modal_header d-flex justify-content-center pt-3'>

                        <h1 className='mt-1'>Header</h1>
                        <button
                            className='btn close_button '
                            onClick={this.props.onClose}
                        >
                            &times;
                        </button>

                    </div>
                    <div className='modal_body'>
                        <h3>Body</h3>
                    </div>
                    <div className='modal_footer'>
                        <h6>Footer</h6>
                    </div>
                </div>
            </div>
        )
    }
}

export default Modal;
