import React from 'react';
import './App.css';
import {client} from './Client';
import Header from './components/Header/Header';
import Login from './components/Login/Login';
import Logout from './components/Logout';
import HomePage from './components/HomePage/HomePage';
import PrivateRoute from "./components/PrivateRoute";
import {Route, Switch} from 'react-router-dom';

class  App extends React.Component {
    state = {
        newses: []
    }
    componentDidMount() {
        if(client.isLoggedIn()){
            client.getNewses(fetchedNewses => {
                this.setState({newses: fetchedNewses});
            })
        }
    }
    loggedIn = () => {
        this.setState({_loggedIn: client.isLoggedIn()})
    }
    render(){
      return (
          <div className="App">
              <Header/>
              <Switch>
                  <PrivateRoute path='/categories' component={Header}/>
                  <PrivateRoute path='/articles' component={Header}/>

                  <Route
                      loggedInCallback={this.loggedIn}
                      path='/login'
                      component={Login}/>
                  <Route path='/logout' component={Logout} />
                  <PrivateRoute exact path='/' component={HomePage}/>
              </Switch>
          </div>
      );
    }
}

export default App;
