import React from 'react';
import './ContactForm.css';
import {isValidEmail} from "../../helpers";
import FormField from '../FormField/FormField'

class ContactForm extends React.Component{
    state = {
        fields: {
            name: '',
            email: '',
            message: ''
        },
        fieldErrors: {}
    }
    onInputChange = ({name, value, error}) => {
        const data = this.state.fields;
        const dataErrors = this.state.fieldErrors;

        data[name] = value;
        dataErrors[name] = error;


    }
    validate = () => {
        const data = this.state.fields;
        const dataErrors = this.state.fieldErrors;
        const errorMessages = Object.keys(dataErrors).filter(key => dataErrors[key]);
        return !data.name || !data.email || !data.message;
    }
    render(){
        return (
            <div>
                <FormField
                    name='name'
                    onChange={this.onInputChange}
                    placeholder='Type your name...'
                    validate={(val) => (val ? false : "Name is required")}
                />
                <FormField
                    name='email'
                    onChange={this.onInputChange}
                    placeholder='Type your email...'
                    validate={(val) => (isValidEmail(val) ? false : "Email is invalid")}
                />
                <FormField
                    name='message'
                    onChange={this.onInputChange}
                    placeholder='Type your email...'
                    validate={(val) => (val ? false : "Email is invalid")}
                />
                <input type="submit" value="Send" disabled={this.validate()}/>
            </div>
        )
    }
}

export default ContactForm;
