import React from 'react';
import './Login.css';
import Loading from '../Loading/Loading';
import LoginForm from '../LoginForm/LoginForm';
import {Redirect} from 'react-router-dom';
import {client} from '../../Client';


class Login extends React.Component{
    state = {
        _loginInProcess: false,
        _loginFailed: false,
        _shouldRedirect: false
    }
    componentDidMount() {
        if(client.isLoggedIn()){
            this.setState({_shouldRedirect: true})
        }
    }

    login = (data, rememberMe) => {
        const {username, password} = data;

        this.setState({_loginInProcess: true},() => {
            client.login({username, password}, rememberMe)
                .then(() => {
                    this.props.loggedInCallback();
                    this.setState({
                        _loginInProcess: false,
                        _shouldRedirect: true
                    })
                })
                .catch(err => {
                    console.log(err.response);
                    this.setState({
                        _loginInProcess: false,
                        _loginFailed: true
                    })
                })
        })
    }
    redirectPath = () => {
        if(!this.props.location) return '/';
        const locationState = this.props.location.state;
        const pathname = (
            locationState && locationState.from && locationState.from.pathname
        );
        return pathname || '/'
    }
    render(){
        if(this.state._shouldRedirect){
            return (
                <Redirect to={this.redirectPath()}/>
            )
        } else if(this.state._loginInProcess) {
            return (
                <div className='mx-auto d-flex align-items-center justify-content-center login_form_container'>
                    <Loading velocity='slow'/>
                </div>

            )
        } else {
            return (
                <div className='mx-auto login_form_container'>
                    <LoginForm
                        longInputs={true}
                        login={this.login}
                        loginFailed={this.state._loginFailed}
                    />
                </div>
            )
        }

    }
}

export default Login;
