import React from 'react';
import './Article.css';
import PropTypes from 'prop-types';



const Article = (props) => {
    const beautifyText = (text) => {
        const searchChar = text.includes(':') ? ':' : '։';
        const splittedArr = text.split(searchChar);
        const resultedArr = [];
        for(let i = 0;i < splittedArr.length;i += 2){
            if(splittedArr[i + 1]){
                resultedArr.push(splittedArr[i] + searchChar + ' ' + splittedArr[i + 1] + searchChar);
            }
        }
        if(splittedArr.length % 2 === 0)
            resultedArr.push(splittedArr[splittedArr.length - 1]);

        return resultedArr;
    }
    console.log(beautifyText(props.content));
    return (
        <article className='pt-4 pb-4'>
            <h1 className='article_heading'>{props.title}</h1>
            <div className='article_content w-75 mx-auto'>
                <img src={props.imgUrl} alt="Not found" className='float-left mr-4'/>
                {
                    beautifyText(props.content).map((textBlock, index) => (
                        <p
                            className='pt-3'
                            key={index}>
                            {textBlock}
                        </p>
                    ))
                }
            </div>
            <div className='info_about_article'>

            </div>
        </article>
    )
}

Article.propTypes = {
    id: PropTypes.string.isRequired,
    title: PropTypes.string.isRequired,
    content: PropTypes.string.isRequired,
    imgUrl: PropTypes.string.isRequired,
    category: PropTypes.string.isRequired,
    createdAt: PropTypes.string.isRequired,
    author: PropTypes.string,
    source: PropTypes.string,
    description: PropTypes.string
}
export default Article;
